import React, { useState } from 'react'
import './Projects.css'

export default function Projects() {
    const data = [
        {name: 'Museam', html: true, react: false, link: 'https://notsoname.github.io/museum/', descr: 'Museam layout', technologies: 'HTML + JS + SCSS + Webpack'},
        {name: 'Momentum', html: true, react: false, link: 'https://notsoname.github.io/momentum/', descr: 'Momentum clone', technologies: 'HTML + JS + CSS'},
        {name: 'Movie APP', html: false, react: true, link: 'https://notsoname.github.io/movieAPP/', descr: 'Movie APP where you can search movies and cartoons', technologies: 'React + Hooks'},
        {name: 'Movie APP', html: false, react: true, link: 'https://notsoname.github.io/weatherApp/', descr: 'Weather APP where you can find out the weather forecase', technologies: 'React + Hooks'},
    ]
    const [filter, setFilter] = useState('all')

    const filterProjects = (items, filter) => {
        switch (filter) {
            case 'html':
                return items.filter(item => item.html);
            case 'react':
                return items.filter(item => item.react);
            default:
                return items
        }
    }

    const onFilterSelect = (filter) => {
        setFilter(filter)
    }
    const visibleData = filterProjects(data, filter);
    return (
        <div>
            <div className='projects-btns'>
                <button onClick={() => onFilterSelect('all')} className='projects-select'>All</button>
                <button onClick={() => onFilterSelect('html')} className='projects-select'>HTML + CSS + JS</button>
                <button onClick={() => onFilterSelect('react')} className='projects-select'>React</button>
            </div>
            <div className='projects'>
            {visibleData.map(item => {
                return <div className='projects-wrapper'>
                    <div>{item.name}</div>
                    <div>{item.descr}</div>
                    <div>{item.technologies}</div>
                    <a href={item.link}>{item.link}</a>
                </div>
            })}
            </div>
        </div>
    )
}
